import "../css/checkoutscreen.css";
import axios from "axios";
import React, { useState } from "react";
import { Button } from "react-bootstrap";
import { toast } from "react-toastify";

function CheckoutScreen() {
  const [success, setSuccess] = useState(false);

  const cart = JSON.parse(localStorage.getItem("cart")) || [];

  // Format giá tiền
  const formatter = new Intl.NumberFormat("vi-VN", {
    style: "currency",
    currency: "VND",
  });

  // Chuyển đổi chuỗi "30.000 đ" => 30000
  const toNum = (str) => {
    return Number(str.split(" ")[0].replace(/\./g, ""));
  };

  // Tính tổng đơn hàng
  let sum = 0;
  cart.forEach((item) => {
    sum += toNum(item.product.priceOn) * item.num;
  });
  const shippingFee = 30000;
  const total = sum + shippingFee;

  // Hàm xử lý khi nhấn "Thanh toán"
  const handlePay = async (e) => {
    e.preventDefault();

    const fullName = document.querySelector('input[name="fullname"]').value;
    const email = document.querySelector('input[name="email"]').value;

    if (!fullName || !email) {
      toast.error("Vui lòng nhập đầy đủ họ tên và email!");
      return;
    }

    try {
      await axios.post("http://localhost:8000/user/create-order", {
        order_id: `DH${Math.floor(Math.random() * 1000000)}`,
        full_name: fullName,
        total: total,
        email: email,
        created_at: "", // để backend tự tạo thời gian
      });

      toast.success("✅ Mua hàng thành công và đã gửi email!");
      setSuccess(true);
      localStorage.setItem("cart", JSON.stringify([]));
      window.dispatchEvent(new Event("storage"));
    } catch (error) {
      toast.error("❌ Gửi đơn hàng thất bại!");
      console.error(error);
    }
  };

  return (
    <div className="container">
      <div className="form">
        <form onSubmit={handlePay}>
          <h3>Thông tin giao hàng</h3>

          <label>Họ và tên <span className="star">*</span></label>
          <input type="text" name="fullname" style={{ width: "100%" }} placeholder="Nguyễn Văn A" />

          <div className="flex-row">
            <div className="flex-col-2">
              <label>Email <span className="star">*</span></label>
              <input type="text" name="email" style={{ width: "100%" }} placeholder="abc@gmail.com" />
            </div>
            <div className="flex-col-2">
              <label>Số điện thoại</label>
              <input type="text" name="phone" style={{ width: "100%" }} placeholder="0948566534" />
            </div>
          </div>

          <label>Địa chỉ <span className="star">*</span></label>
          <input type="text" name="address" style={{ width: "100%" }} placeholder="123 ABC, TP.HCM" />

          <div className="separation"></div>

          <p>Phương thức thanh toán</p>
          <div className="radio-item">
            <label>
              <img
                alt=""
                src="https://cdn-icons-png.flaticon.com/512/5720/5720434.png"
              />
              Thanh toán khi nhận hàng
            </label>
            <input type="radio" id="COD" name="pay-method" value="COD" defaultChecked />
          </div>

          <div className="separation"></div>

          <div className="end-form">
            <p>Giá trị đơn hàng</p>
            <p>{formatter.format(sum)}</p>
          </div>

          <div className="end-form">
            <p>Phí vận chuyển</p>
            <p>{formatter.format(shippingFee)}</p>
          </div>

          <div className="end-form">
            <p>Tổng cộng</p>
            <p>{formatter.format(total)}</p>
          </div>

          <div className="checkout-button">
            <Button variant="success" onClick={() => window.history.back()}>
              Về giỏ hàng
            </Button>
            <Button variant="primary" type="submit">
              Thanh toán
            </Button>
          </div>

          {success && (
            <div style={{ color: "green", marginTop: 20, fontWeight: "bold", textAlign: "center" }}>
              Mua hàng thành công!
            </div>
          )}
        </form>
      </div>
    </div>
  );
}

export default CheckoutScreen;
