import "../css/cartscreen.css";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Button } from "react-bootstrap";

const CartScreen = () => {
  const navigate = useNavigate();

  const [cart, setCart] = useState(
    localStorage.getItem("cart")
      ? JSON.parse(localStorage.getItem("cart"))
      : []
  );

  const handleRemove = (item) => {
    let newCart;
    if (item.num === 1) {
      newCart = cart.filter((x) => x.product.name !== item.product.name);
    } else {
      newCart = cart.map((x) =>
        x.product.name === item.product.name ? { ...x, num: x.num - 1 } : x
      );
    }
    setCart(newCart);
    localStorage.setItem("cart", JSON.stringify(newCart));
    window.dispatchEvent(new Event("storage"));
  };

  const formatter = new Intl.NumberFormat("vi-VN", {
    style: "currency",
    currency: "VND",
  });

  const toNum = (str) => {
    return Number(str.split(" ")[0].replace(/\./g, ""));
  };

  const total = cart.reduce(
    (sum, item) => sum + toNum(item.product.priceOn) * item.num,
    0
  );

  return (
    <div className="cart">
      <div className="table-container">
        <table className="table table-striped">
          <thead>
            <tr>
              <th>#</th>
              <th>Hình ảnh</th>
              <th>Sản phẩm</th>
              <th>Đơn giá</th>
              <th>Số lượng</th>
              <th>Xóa</th>
            </tr>
          </thead>
          <tbody>
            {cart.map((item, index) => (
              <tr key={index} className={index % 2 ? "RowEven" : "RowOdd"}>
                <td>{index + 1}</td>
                <td>
                  <img
                    className="cart-img"
                    src={item.product.image}
                    alt={item.product.label}
                  />
                </td>
                <td>{item.product.name}</td>
                <td>{item.product.priceOn}</td>
                <td>{item.num}</td>
                <td>
                  <button
                    className="cart-row-remove"
                    onClick={() => handleRemove(item)}
                  >
                    <img
                      src={require("../img/delete.png")}
                      alt="Xóa"
                    />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <h5>Tổng tiền: {formatter.format(total)}</h5>
      </div>

      <div className="checkout">
        <Button variant="primary" onClick={() => navigate("/checkout")}>
          Thanh toán
        </Button>
      </div>
    </div>
  );
};

export default CartScreen;
