import React, { useState } from 'react';
import '../css/detail.css';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import closeSearch from '../icon-font/close-icon.svg';
import labeldata from '../labeldata';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const data = labeldata.thefifth;

const CompareForm = styled.div`
  background-color: #fff;
  box-shadow: 1px 1px 1px 1px #ccc;
  border-radius: 20px;
  padding-bottom: 10px;
  height: 100%;
  width: 50%;
  padding: 16px;
  gap: 16px;
`;

const InputForm = styled.div`
  padding: 0px;
  gap: 9.7px;
  max-width: 100%;
  display: inline;
  flex-direction: column;
  align-items: flex-end;
  input {
    box-sizing: border-box;
    align-items: flex-start;
    width: 100%;
    height: 50px;
    border: 1px solid rgba(36, 89, 173, 1);
    border-radius: 10px;
    &:focus {
      outline: 2px solid rgba(36, 89, 173, 1);
    }
  }
`;

const LabelLine = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  padding: 0px;
  gap: 4.85px;
  height: 24px;
  margin-bottom: 10px;
`;

const LabelSearch = styled.label`
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 20px;
  margin-bottom: 0px;
`;

const SearchResults = styled.div`
  width: 100%;
  height: 210px;
  background-color: white;
  box-shadow: rgba(0, 0, 0, 0.35) 0px 5px 15px;
  overflow: hidden;
  overflow-y: auto;
`;

const ProductItem = styled(Link)`
  width: 100%;
  height: 70px;
  display: flex;
  align-items: center;
  color: black;
  text-decoration: none;
  cursor: pointer;
  border-bottom: 1px solid #ccc;
  &:hover {
    background-color: lightgrey;
  }
  img {
    max-width: 100%;
    max-height: 100%;
  }
`;

function Compare({ open, onClose, handle }) {
  const [filteredData, setFilteredData] = useState([]);
  const [wordEntered, setWordEntered] = useState('');
  const [hideInfo, setHideInfo] = useState(true);
  const [showCompare, setShowCompare] = useState(false);
  const [product, setProduct] = useState([]);

  const handleFilter = (event) => {
    const searchWord = event.target.value;
    setWordEntered(searchWord);
    const newFilter = data.filter((value) =>
      value.name.toLowerCase().includes(searchWord.toLowerCase())
    );
    if (searchWord === '') {
      setFilteredData([]);
    } else {
      setFilteredData(newFilter);
    }
  };

  const appear = (value) => {
    handle();
    setShowCompare(true);
    setProduct(value);
  };

  const turnBack = () => {
    onClose();
    setShowCompare(false);
  };

  const handleBuy = () => {
    let cart = localStorage.getItem('cart') ? JSON.parse(localStorage.getItem('cart')) : [];
    const found = cart.find((item) => item.product.name === product.name);
    if (!found) {
      cart.push({ product: product, num: 1 });
    } else {
      found.num++;
    }
    localStorage.setItem('cart', JSON.stringify(cart));
    toast('Bạn đã thêm vào giỏ hàng thành công!');
    window.dispatchEvent(new Event('storage'));
  };

  return (
    <>
      {open && (
        <CompareForm>
          <InputForm>
            <LabelLine>
              <LabelSearch>Nhập tên sản phẩm</LabelSearch>
              <img onClick={onClose} src={closeSearch} className="close-outline-btn" alt="" />
            </LabelLine>
            <input
              type="text"
              value={wordEntered}
              placeholder="..."
              onChange={handleFilter}
            />
            {filteredData.length !== 0 && (
              <SearchResults>
                {filteredData.slice(0, 17).map((value, index) => (
                  <ProductItem key={value._id || index} onClick={() => appear(value)}>
                    <img src={value.image} alt="" />
                    <p>{value.name}</p>
                  </ProductItem>
                ))}
              </SearchResults>
            )}
          </InputForm>
        </CompareForm>
      )}

      {showCompare && (
        <div className="detail-container product3">
          <div className="center">
            <img src={product.image} alt="" />
          </div>
          <div className="under">
            <div className="price">
              <ion-icon className="price-tag-icon" name="pricetags-outline" />
              <div className="price-text">Giá bán</div>
              <div className="price-self">{product.priceOn}</div>
            </div>
            <div className="more">
              <ion-icon name="heart-circle-outline" className="heart-btn" />
              <ion-icon name="share-social-outline" className="share-btn" />
            </div>
          </div>
          <form action="" className="style-form">
            Màu sắc
            <input type="radio" name="form-detail" defaultValue="đen" />
            <label>Đen</label>
            <input type="radio" name="form-detail" defaultValue="xám" />
            <label>Xám</label>
          </form>
          <div className="name-product">{product.name}</div>
          <div className="btn-group">
            <button className="btn-shopping" onClick={handleBuy}>
              <ion-icon name="cart-outline" className="shopping-cart"></ion-icon>
              Mua ngay
            </button>
            <button className="btn-close-outline">
              <ion-icon onClick={turnBack} name="close" className="close-outline-pop"></ion-icon>
            </button>
          </div>
          <div className="product-describe">
            Thông tin mô tả
            <ul>
              <p>{product.title}</p>
              {product.describe.map((line, index) => (
                <li key={index}>{line}</li>
              ))}
            </ul>
          </div>
          <div className="more-detail">
            <div className="detail">Thông tin chi tiết</div>
            <div className="hide" onClick={() => setHideInfo(!hideInfo)}>
              {hideInfo ? 'Ẩn bớt' : 'Xem thêm'}
              {hideInfo ? (
                <ion-icon name="chevron-up-outline" />
              ) : (
                <ion-icon name="chevron-down-outline" />
              )}
            </div>
          </div>
          {hideInfo && (
            <div className="detail-product">
              <div className="detail-product-item">
                <div className="field">Chất liệu:</div>
                <div className="desc">Vải cotton co giãn, thoáng mát</div>
              </div>
              <div className="detail-product-item">
                <div className="field">Phong cách:</div>
                <div className="desc">Năng động, thời trang</div>
              </div>
              <div className="detail-product-item">
                <div className="field">Xuất xứ:</div>
                <div className="desc">Việt Nam / Hàn Quốc / Trung Quốc</div>
              </div>
              <div className="detail-product-item">
                <div className="field">Thương hiệu:</div>
                <div className="desc">{product.label}</div>
              </div>
              <div className="detail-product-item">
                <div className="field">Size:</div>
                <div className="desc">S, M, L</div>
              </div>
            </div>
          )}
        </div>
      )}
    </>
  );
}

export default Compare;
