import "../css/cartscreen.css";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Button } from "react-bootstrap";

const CartScreen = () => {
  const navigate = useNavigate();

  const [cart, setCart] = useState(() => {
    const storedCart = localStorage.getItem("cart");
    try {
      const parsed = JSON.parse(storedCart);
      return Array.isArray(parsed) ? parsed : [parsed];
    } catch {
      return [];
    }
  });

  const handleRemove = (item) => {
    if (item.num === 1) {
      const filtered = cart.filter((x) => x.product.name !== item.product.name);
      setCart(filtered);
      localStorage.setItem("cart", JSON.stringify(filtered));
    } else {
      const updated = cart.map((x) => {
        if (x.product.name === item.product.name) x.num--;
        return x;
      });
      setCart(updated);
      localStorage.setItem("cart", JSON.stringify(updated));
    }

    window.dispatchEvent(new Event("storage"));
  };

  const formatter = new Intl.NumberFormat("en-US", {
    style: "currency",
    currency: "VND",
  });

  const toNum = (str) => {
    return Number(str.split(" ")[0].replace(/\./g, ""));
  };

  let sum = 0;
  cart.forEach((x) => {
    sum += toNum(x.product.priceOn) * x.num;
  });

  return (
    <div className="cart">
      <div className="table-container">
        <table className="table table-striped">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Hình ảnh</th>
              <th scope="col">Sản phẩm</th>
              <th scope="col">Đơn Giá</th>
              <th scope="col">Số lượng</th>
              <th scope="col">Tùy chỉnh</th>
            </tr>
          </thead>

          <tbody>
            {cart.map((item, index) => (
              <tr key={index} className={index % 2 ? "RowEven" : "RowOdd"}>
                <th scope="row">{index + 1}</th>
                <td>
                  <img
                    className="cart-img"
                    src={item.product.image}
                    alt={item.product.label || "Hình ảnh sản phẩm"}
                  />
                </td>
                <td>{item.product.name}</td>
                <td>{item.product.priceOn}</td>
                <td>{item.num}</td>
                <td>
                  <button
                    className="cart-row-remove"
                    onClick={() => handleRemove(item)}
                  >
                    <img src={require("../img/delete.png")} alt="Xóa" />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <h5>Tổng tiền: {formatter.format(sum)}</h5>
      </div>

      <div className="checkout">
        <Button variant="primary" onClick={() => navigate("/checkout")}>
          Thanh toán
        </Button>
      </div>
    </div>
  );
};

export default CartScreen;
