import React from 'react';
import labeldata from '../labeldata';
import { Link } from 'react-router-dom';
import '../css/product.css';

function Content() {
  const featured = labeldata.thefifth.slice(0, 4); // 4 sản phẩm đầu

  return (
    <div className="content-container">
      {featured.map((item, index) => (
        <div className="item" key={index}>
          <div className="sale">{item.sale}</div>
          <img src={item.image} alt={item.name} className="DPi-image" />
          <div className="DPi-name">{item.name}</div>
          <div className="DPi-label">{item.label}</div>
          <div className="DPi-price">
            <span className="DPi-price-off">{item.priceOff}</span>
            <span className="DPi-price-on">{item.priceOn}</span>
          </div>
          <Link to={`/product/${item._id}`}>
            <div className="DP-buynow">Mua ngay</div>
          </Link>
        </div>
      ))}
    </div>
  );
}

export default Content;
