import "../css/cartscreen.css";
import { useState, useEffect } from "react";
import { Navigate } from "react-router-dom";
import { Button } from "react-bootstrap";

const CartScreen = () => {
  const [cart, setCart] = useState(() => {
    const stored = localStorage.getItem("cart");
    if (!stored) return [];

    try {
      const parsed = JSON.parse(stored);

      // Hợp lệ: item có product.priceOn hoặc chính item.priceOn
      const valid = parsed.filter((x) =>
        (x.product && x.product.priceOn && x.num) ||
        (x.priceOn && x.name && x.num)
      );

      return valid;
    } catch {
      return [];
    }
  });

  const handleRemove = (item) => {
    const itemName = item?.product?.name || item?.name;
    if (!itemName) return;

    if (item.num === 1) {
      const updated = cart.filter(
        (x) => (x.product?.name || x.name) !== itemName
      );
      setCart(updated);
      localStorage.setItem("cart", JSON.stringify(updated));
    } else {
      const updated = cart.map((x) => {
        if ((x.product?.name || x.name) === itemName) {
          x.num -= 1;
        }
        return x;
      });
      setCart(updated);
      localStorage.setItem("cart", JSON.stringify(updated));
    }

    window.dispatchEvent(new Event("storage"));
  };

  const formatter = new Intl.NumberFormat("en-US", {
    style: "currency",
    currency: "VND",
  });

  const toNum = (str) => {
    return Number(str.split(" ")[0].replace(/\./g, ""));
  };

  let sum = 0;

  cart.forEach((x) => {
    const price = x.product?.priceOn || x.priceOn;
    if (price) sum += toNum(price) * x.num;
  });

  return (
    <div className="cart">
      <div className="table-container">
        <table className="table table-striped">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Hình ảnh</th>
              <th scope="col">Sản phẩm</th>
              <th scope="col">Đơn Giá</th>
              <th scope="col">Số lượng</th>
              <th scope="col">Tùy chỉnh</th>
            </tr>
          </thead>
          <tbody>
            {cart.map((item, index) => {
              const p = item.product || item;
              return (
                <tr key={index} className={index % 2 ? "RowEven" : "RowOdd"}>
                  <th scope="row">{index + 1}</th>
                  <td>
                    <img className="cart-img" src={p.image} alt={p.label} />
                  </td>
                  <td>{p.name}</td>
                  <td>{p.priceOn}</td>
                  <td>{item.num}</td>
                  <td>
                    <button
                      className="cart-row-remove"
                      onClick={() => handleRemove(item)}
                    >
                      <img src={require("../img/delete.png")} alt="" />
                    </button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
        <h5>Tổng tiền: {formatter.format(sum)}</h5>
      </div>

      <div className="checkout">
        <a href="/checkout">
          <Button
            variant="primary"
            onClick={() => <Navigate to="/checkout" />}
          >
            Thanh toán
          </Button>
        </a>
      </div>
    </div>
  );
};

export default CartScreen;
